import React, { useState } from "react";

function App() {
	const [count, setCount] = useState(0);
	const decrementCount = () => {
		setCount(count++);
	};

	return (
		<>
			<button onClick={decrementCount}>-</button>
			<span>{count}</span>
			<button>+</button>
		</>
	);
}

export default App;
